mapper.select:
SELECT name
FROM member
WHERE id = #{Id}

SELECT name, author
FROM member
WHERE id = #{Id} AND password = #{Password}

mapper.update:
		UPDATE member 
		SET name = #{Name}, password = #{Password}, tel = #{tel, jdbcType=VARCHAR},
		           address = #{Address, jdbcType=VARCHAR}, author = #{Author, jdbcType=VARCHAR}
        WHERE id = #{Id}
